import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AppContext } from "../../Context/AppContext";
import "./ResourcePage.css";

const ResourcePage = () => {
  const { username: currentUsername } = useContext(AppContext);

  const [resources, setResources] = useState([]);
  const [newResourceName, setNewResourceName] = useState("");
  const [newResourceCode, setNewResourceCode] = useState("");
  const [selectedResourceDetail, setSelectedResourceDetail] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8085/resources")
      .then((response) => {
        setResources(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleResourceClick = (resourceId) => {
    axios
      .get(`http://localhost:8085/resource-details/${resourceId}`)
      .then((response) => {
        setSelectedResourceDetail(response.data);
      })
      .catch((error) => {
        console.error("Error fetching resource details:", error);
      });
  };

  const handleDetailChange = (e) => {
    const { name, value } = e.target;
    setSelectedResourceDetail((prevDetail) => ({
      ...prevDetail,
      [name]: value,
    }));
  };

  const addResource = () => {
    const resourceDTO = {
      resourceName: newResourceName,
      resourceCode: newResourceCode,
    };

    axios
      .post("http://localhost:8085/resources", resourceDTO)
      .then((response) => {
        setResources([...resources, response.data]);
        setNewResourceName("");
        setNewResourceCode("");
      })
      .catch((error) => {
        console.error("Error adding resource:", error);
      });
  };

  const updateResourceDetail = () => {
    if (selectedResourceDetail) {
      axios
        .put(
          `http://localhost:8085/resource-details/${selectedResourceDetail.id}`,
          selectedResourceDetail
        )
        .then(() => {
          console.log("Resource detail updated successfully.");
        })
        .catch((error) => {
          console.error("Error updating resource details:", error);
        });
    }
  };

  return (
    <div className="resource-container">
      <div className="search-bar">
        <input type="text" placeholder="Keyword" />
        <button>üîç</button>
      </div>

      <div className="resource-list">
        <h3>Resource Catalog</h3>
        <div className="resource-table">
          {resources.map((resource) => (
            <div
              className="resource-row"
              key={resource.resourceId}
              onClick={() => handleResourceClick(resource.resourceId)}
            >
              <span>{resource.resourceName}</span>
              <span>{resource.resourceCode}</span>
            </div>
          ))}

          <div className="resource-input">
            <input
              type="text"
              placeholder="Resource Name"
              value={newResourceName}
              onChange={(e) => setNewResourceName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Resource Code"
              value={newResourceCode}
              onChange={(e) => setNewResourceCode(e.target.value)}
            />
            <button onClick={addResource}>+</button>
          </div>
        </div>
      </div>

      {selectedResourceDetail && (
        <div className="resource-detail-editor">
          <h4>Edit Resource Details</h4>
          <input
            type="text"
            name="resourceCode"
            placeholder="Resource Code"
            value={selectedResourceDetail.resourceCode}
            onChange={handleDetailChange}
          />
          <input
            type="text"
            name="resourceDescription"
            placeholder="Resource Description"
            value={selectedResourceDetail.resourceDescription}
            onChange={handleDetailChange}
          />
          <button onClick={updateResourceDetail}>Update Details</button>
        </div>
      )}
    </div>
  );
};

export default ResourcePage;
