import React, { useState } from "react";
import { useNavigation } from "react-router-dom";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory();

  const handleLoginClick = () => {
    // Simple hard-coded check for demonstration purposes.
    if (username === "aaa@gmail.com" && password === "password123") {
      onLogin(username); // Inform the parent component of a successful login.
      history.push("/resources"); // Navigate to the resource page.
    } else {
      alert("Invalid username or password.");
    }
  };

  return (
    <div className="login-container">
      {/* ... other JSX elements ... */}
      <button onClick={handleLoginClick}>Login</button>
    </div>
  );
}

export default Login;
