import React, { useState } from "react";
import "./Sidebar.css";

function Sidebar({ setCurrentPage, isLoggedIn }) {
  const [isExtended, setIsExtended] = useState(isLoggedIn);

  const handleItemClick = (page) => {
    setCurrentPage(page);
    setIsExtended(!isExtended);
  };

  return (
    <div
      className={`sidebar ${isExtended ? "extended" : ""}`}
      onClick={() => setIsExtended(!isExtended)}
    >
      {/* You can add an icon here for a visual cue */}
      <ul>
        <li onClick={() => handleItemClick("Resource")}>Resource</li>
        <li onClick={() => handleItemClick("Project")}>Project</li>
        <li onClick={() => handleItemClick("Formula")}>Formula</li>
      </ul>
    </div>
  );
}

export default Sidebar;
