import React from "react";
import { connect } from "react-redux";
import { setLoginStatus } from "./Redux/Actions/appActions"; // Path might vary based on your project structure
import Navbar from "./Components/Navbar/Navbar";
import SidebarToggler from "./Components/SidebarToggler/SidebarToggler";
import Sidebar from "./Components/Sidebar/Sidebar";
import Login from "./Pages/Login/Login";
import RegistrationPage from "./Pages/RegistrationPage/RegistrationPage";
import ResourcePage from "./Pages/ResourcePage/ResourcePage";
import Project from "./Pages/Project/Project";
import Formula from "./Pages/Formula/Formula";
import "./App.css";

function App(props) {
  const [isSidebarExtended, setIsSidebarExtended] = useState(false);

  // Use the Redux state instead of the local state
  const isLoggedIn = props.isLoggedIn;

  // State for current page view (login or registration)
  const [page, setPage] = useState("login");

  return (
    <div>
      <Navbar />
      <div className="container">
        {!isLoggedIn && (
          <div>
            {page === "login" && <Login setPage={setPage} />}
            {page === "registration" && <RegistrationPage setPage={setPage} />}
          </div>
        )}
        {isLoggedIn && (
          <div className="sidebar-container">
            {isSidebarExtended && <Sidebar setPage={setPage} />}
            <SidebarToggler
              isExtended={isSidebarExtended}
              toggle={() => setIsSidebarExtended(!isSidebarExtended)}
            />
          </div>
        )}
        <div className="main-content">
          {isLoggedIn && page === "Resource" && <ResourcePage />}
          {isLoggedIn && page === "Project" && <Project />}
          {isLoggedIn && page === "Formula" && <Formula />}
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  isLoggedIn: state.isLoggedIn,
});

export default connect(mapStateToProps, { setLoginStatus })(App);
