import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AppContext } from "../../../Context/AppContext";
import "./ProjectPage.css";

const ProjectPage = () => {
  const { username } = useContext(AppContext);
  const [projects, setProjects] = useState([]);
  const [resources, setResources] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [linkedResourceIds, setLinkedResourceIds] = useState(new Set());
  const [newProjectName, setNewProjectName] = useState("");

  // Pagination states
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  const fetchProjectById = async (projectId) => {
    try {
      const response = await axios.get(
        `http://localhost:8085/projects/${projectId}`
      );
      const updatedProject = response.data;
      setSelectedProject(updatedProject);
      setLinkedResourceIds(new Set(updatedProject.resourceIds));
    } catch (error) {
      console.error(`Error fetching project with ID ${projectId}:`, error);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const projectsResponse = await axios.get(
          "http://localhost:8085/projects"
        );
        const filteredProjects = projectsResponse.data.filter(
          (p) => p.userName === username
        );
        setProjects(filteredProjects);

        const resourcesResponse = await axios.get(
          "http://localhost:8085/resources"
        );
        setResources(resourcesResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [username]);

  const currentProjects = projects.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const totalPages = Math.ceil(projects.length / itemsPerPage);

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(
        <span
          key={i}
          className={currentPage === i ? "active-page" : "page-number"}
          onClick={() => setCurrentPage(i)}
        >
          {i}
        </span>
      );
    }
    return pageNumbers;
  };
  return (
    <div className="project-container">
      <div className="new-project-container">
        <input
          type="text"
          placeholder="New Project Name"
          value={newProjectName}
          onChange={(e) => setNewProjectName(e.target.value)}
        />
        <button onClick={handleCreateProject}>Create Project</button>
      </div>

      <div className="project-dropdown">
        <select
          value={selectedProject?.projectId || ""}
          onChange={(e) => {
            const projectId = parseInt(e.target.value, 10);
            const chosenProject = projects.find(
              (p) => p.projectId === projectId
            );
            fetchProjectById(projectId);
          }}
        >
          <option value="">Select a Project</option>
          {currentProjects.map((project) => (
            <option key={project.projectId} value={project.projectId}>
              {project.projectName}
            </option>
          ))}
        </select>
      </div>

      <div className="content-wrapper">
        <div className="resources-list">
          <h3>Unlinked Resources</h3>
          {resources.map(
            (resource) =>
              !linkedResourceIds.has(resource.resourceId) && (
                <div key={resource.resourceId}>
                  <span>{resource.resourceName}</span>
                  <button
                    className="link-unlink-button"
                    onClick={() => linkResourceToProject(resource.resourceId)}
                  >
                    +
                  </button>
                </div>
              )
          )}
        </div>

        <div className="selected-resources-list">
          <h3>
            Linked Resources for Project:{" "}
            {selectedProject?.projectName || "None"}
          </h3>
          {Array.from(linkedResourceIds).map((resourceId) => {
            const resource = resources.find((r) => r.resourceId === resourceId);
            return (
              <div key={resourceId}>
                <span>{resource.resourceName}</span>
                <button
                  className="link-unlink-button"
                  onClick={() => unlinkResourceFromProject(resourceId)}
                >
                  -
                </button>
              </div>
            );
          })}
        </div>
      </div>

      <div className="pagination">{renderPageNumbers()}</div>
    </div>
  );
};
export default ProjectPage;
