import React, { useState } from "react";
import Navbar from "./Components/Navbar/Navbar";
import Sidebar from "./Components/Sidebar/Sidebar";
import Login from "./Pages/Login/Login";
import Resource from "./Pages/ResourcePage/ResourcePage";
import Project from "./Pages/Project/Project"; // Assuming you have this page
import Formula from "./Pages/Formula/Formula"; // Assuming you have this page
import "./App.css"; // Importing the CSS

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentPage, setCurrentPage] = useState("Resource"); // Default page is Resource

  return (
    <div>
      <Navbar setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} />

      <div className="container">
        <div className="sidebar">
          <Sidebar setCurrentPage={setCurrentPage} />{" "}
          {/* Pass the setCurrentPage function to Sidebar if you need to change the page from there */}
        </div>
        <div className="main-content">
          {!isLoggedIn && <Login setIsLoggedIn={setIsLoggedIn} />}
          {isLoggedIn && currentPage === "Resource" && <Resource />}
          {isLoggedIn && currentPage === "Project" && <Project />}
          {isLoggedIn && currentPage === "Formula" && <Formula />}
        </div>
      </div>
    </div>
  );
}

export default App;
