import React, { useState } from "react";
import Navbar from "./Components/Navbar/Navbar";
import SidebarToggler from "./Components/SidebarToggler/SidebarToggler";
import Sidebar from "./Components/Sidebar/Sidebar";
import Login from "./Pages/Login/Login";
import RegistrationPage from "./Pages/RegistrationPage/RegistrationPage";
import ResourcePage from "./Pages/ResourcePage/ResourcePage";
import Project from "./Pages/Project/ProjectPage";
import Formula from "./Pages/Formula/Formula";
import { AppContext } from "./Context/AppContext";
import "./App.css";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isSidebarExtended, setIsSidebarExtended] = useState(false);
  const [page, setPage] = useState("login");
  const [username, setUsername] = useState(""); // State to hold the logged-in username

  const contextValue = {
    isLoggedIn,
    setIsLoggedIn,
    isSidebarExtended,
    setIsSidebarExtended,
    page,
    setPage,
    username,
    setUsername,
  };

  return (
    <AppContext.Provider value={contextValue}>
      <div>
        <Navbar />
        <div className="container">
          {!isLoggedIn && (
            <div>
              {page === "login" && <Login />}
              {page === "registration" && <RegistrationPage />}
            </div>
          )}
          {isLoggedIn && (
            <div className="sidebar-container">
              {isSidebarExtended && <Sidebar />}
              <SidebarToggler />
            </div>
          )}
          <div className="main-content">
            {isLoggedIn && page === "Resource" && <ResourcePage />}
            {isLoggedIn && page === "Project" && <Project />}
            {isLoggedIn && page === "Formula" && <Formula />}
          </div>
        </div>
      </div>
    </AppContext.Provider>
  );
}

export default App;
