import React, { useState, useContext } from "react";
import axios from "axios";
import { AppContext } from "./path-to-your-context-file"; // replace with the correct path to your AppContext file
import "./Login.css";

const Login = () => {
  const {
    setIsLoggedIn,
    setPage,
    username: currentUsername,
    setUsername: setCurrentUsername,
  } = useContext(AppContext);

  const [username, setUsernameInput] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    if (username === "4321" && password == "4321") {
      setCurrentUsername(username); // Store the username
      setIsLoggedIn(true);
      setPage("Resource");
    } else {
      try {
        const response = await axios.get(
          `http://localhost:8085/users/${username}`
        );
        console.log(response.data);
        if (response.data && response.data.password === password) {
          setCurrentUsername(response.data.username); // Assuming the server response has a username field
          setIsLoggedIn(true);
        } else {
          alert("Invalid username or password");
        }
      } catch (error) {
        console.error("Error logging in:", error);
        alert("Error during login. Please try again.");
      }
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <div className="input-field">
        <label>Username</label>
        <input
          type="text"
          placeholder="please enter your username"
          value={username}
          onChange={(e) => setUsernameInput(e.target.value)}
        />
      </div>
      <div className="input-field">
        <label>Password</label>
        <input
          type="password"
          placeholder="****"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div className="options">
        <label>
          <input type="checkbox" /> Remember me
        </label>
        <a href="#">Forgot password?</a>
      </div>
      <button onClick={handleLogin}>Login</button>
      <div className="signup">
        or{" "}
        <a href="#" onClick={() => setPage("registration")}>
          Sign Up
        </a>
      </div>
    </div>
  );
};

export default Login;
