import React, { useState } from "react";
import Navbar from "./Components/Navbar/Navbar";
import SidebarToggler from "./Components/SidebarToggler/SidebarToggler";
import Sidebar from "./Components/Sidebar/Sidebar";
import Login from "./Pages/Login/Login";
import Resource from "./Pages/Resource/Resource";
import Project from "./Pages/Project/Project";
import Formula from "./Pages/Formula/Formula";
import "./App.css";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isSidebarExtended, setIsSidebarExtended] = useState(false); // New state for sidebar toggle
  const [currentPage, setCurrentPage] = useState("Resource");

  return (
    <div>
      <Navbar setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} />
      <div className="container">
        {isLoggedIn && (
          <SidebarToggler
            isExtended={isSidebarExtended}
            toggle={() => setIsSidebarExtended(!isSidebarExtended)}
          />
        )}
        {isLoggedIn && isSidebarExtended && (
          <Sidebar setCurrentPage={setCurrentPage} />
        )}
        <div className="main-content">
          {!isLoggedIn && <Login setIsLoggedIn={setIsLoggedIn} />}
          {isLoggedIn && currentPage === "Resource" && <Resource />}
          {isLoggedIn && currentPage === "Project" && <Project />}
          {isLoggedIn && currentPage === "Formula" && <Formula />}
        </div>
      </div>
    </div>
  );
}

export default App;
