import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AppContext } from "../../Context/AppContext";
import "./ResourcePage.css";

const ResourcePage = () => {
  const { username: currentUsername } = useContext(AppContext);

  const [resources, setResources] = useState([]);
  const [editingResource, setEditingResource] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8085/resources")
      .then((response) => {
        setResources(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleRowClick = (resource) => {
    setEditingResource({ ...resource });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditingResource((prevResource) => ({
      ...prevResource,
      [name]: value,
    }));
  };

  const confirmUpdates = () => {
    if (editingResource) {
      axios
        .put(
          `http://localhost:8085/resource-details/${editingResource.id}`,
          editingResource
        )
        .then(() => {
          const updatedResources = resources.map((resource) =>
            resource.id === editingResource.id ? editingResource : resource
          );
          setResources(updatedResources);
          setEditingResource(null);
        })
        .catch((error) => {
          console.error("Error updating resource details:", error);
        });
    }
  };

  return (
    <div className="resource-container">
      <table>
        <thead>
          <tr>
            <th>Resource Name</th>
            <th>Resource Code</th>
            <th>Description</th>
            <th>Creation Date</th>
            <th>Modified Date</th>
            <th>Cost</th>
          </tr>
        </thead>
        <tbody>
          {resources.map((resource) => (
            <tr key={resource.id} onClick={() => handleRowClick(resource)}>
              <td>{resource.resourceName}</td>
              <td>{resource.resourceCode}</td>
              <td>{resource.resourceDescription}</td>
              <td>{resource.creationDate}</td>
              <td>{resource.modifiedDate}</td>
              <td>{resource.resourceCost}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingResource && (
        <div className="resource-detail-editor">
          <h4>Edit Resource Details</h4>
          <label>
            Resource Code:
            <input
              type="text"
              name="resourceCode"
              value={editingResource.resourceCode}
              onChange={handleInputChange}
            />
          </label>
          <label>
            Description:
            <input
              type="text"
              name="resourceDescription"
              value={editingResource.resourceDescription}
              onChange={handleInputChange}
            />
          </label>
          {/* Add other fields as necessary */}
        </div>
      )}

      <button onClick={confirmUpdates}>Confirm Updates</button>
    </div>
  );
};

export default ResourcePage;
