import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AppContext } from "../../Context/AppContext";
import "./Formula.css";

function Formula() {
  const { username: currentUsername } = useContext(AppContext);
  const [data, setData] = useState([]);

  const [newFormula, setNewFormula] = useState({
    formulaName: "",
    formulaCostCode: "",
    edited: false,
    id: "", // Assuming you have some mechanism to assign IDs. If IDs are auto-generated by backend, you don't need this here.
  });

  useEffect(() => {
    axios
      .get("http://localhost:8085/formulas")
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching formula data:", error);
      });
  }, []);

  const handleAddFormula = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8085/formulas",
        newFormula
      );
      setData((prevData) => [...prevData, response.data]);
      setNewFormula({
        formulaName: "",
        formulaCostCode: "",
        edited: false,
        id: "",
      });
    } catch (error) {
      console.error("Error adding new formula:", error);
    }
  };

  return (
    <div className="formula-page">
      {/* Add Formula Section */}
      <div className="add-formula">
        <input
          placeholder="Formula Name"
          value={newFormula.formulaName}
          onChange={(e) =>
            setNewFormula({ ...newFormula, formulaName: e.target.value })
          }
        />
        <input
          placeholder="Cost Code"
          value={newFormula.formulaCostCode}
          onChange={(e) =>
            setNewFormula({ ...newFormula, formulaCostCode: e.target.value })
          }
        />
        <button onClick={handleAddFormula}>Add Formula</button>
      </div>

      {/* Formula Table */}
      <table>
        <thead>
          <tr>
            <th>NAME</th>
            <th>COST_CODE</th>
            <th>EDITABLE</th>
            <th>ITEM_ID</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id}>
              <td>{item.formulaName}</td>
              <td>{item.formulaCostCode}</td>
              <td>{item.edited ? "Yes" : "No"}</td>
              <td>{item.id}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Formula;
