import React from "react";
import "./Sidebar.css";
import { useState } from "react";

function Sidebar({ setCurrentPage }) {
  // Adding the setCurrentPage prop
  const [isExtended, setIsExtended] = useState(false);

  const handleItemClick = (page) => {
    setCurrentPage(page); // Set the current page
    setIsExtended(!isExtended); // Optionally, collapse the sidebar after selecting an item
  };

  return (
    <div
      className={`sidebar ${isExtended ? "extended" : ""}`}
      onClick={() => setIsExtended(!isExtended)}
    >
      <ul>
        <li onClick={() => handleItemClick("Resource")}>Resource</li>
        <li onClick={() => handleItemClick("Project")}>Project</li>
        <li onClick={() => handleItemClick("Formula")}>Formula</li>
        {/* Add more items as needed */}
      </ul>
    </div>
  );
}

export default Sidebar;
